USE master
GO
IF EXISTS (SELECT *FROM sys.databases WHERE name = 'MovieManagement')
BEGIN
    alter database MovieManagement set single_user with rollback immediate
    DROP DATABASE MovieManagement
END


-- Tạo lại cơ sở dữ liệu MovieManagement
CREATE DATABASE MovieManagement;
GO

-- Sau khi tạo lại cơ sở dữ liệu, tạo các bảng
USE MovieManagement;
GO

-- Tạo bảng users
CREATE TABLE users (
  user_id INT PRIMARY KEY IDENTITY,
  username NVARCHAR(255),
  password NVARCHAR(255),
  avatar NVARCHAR(255),
  fullname NVARCHAR(255),
  birthday DATE,
  gender INT ,
  email NVARCHAR(255),
  city NVARCHAR(255),
  phone NVARCHAR(20),
  point INT
);

CREATE TABLE Person (
    ID INT PRIMARY KEY,
    Name NVARCHAR(255),
    Avatar NVARCHAR(255),
    Biography NVARCHAR(MAX)
);


-- Tạo bảng cinemas
CREATE TABLE cinemas (
  cinema_id INT PRIMARY KEY ,
  cinema_name NVARCHAR(255),
);

-- Tạo bảng movies

CREATE TABLE movies (
  movie_id INT PRIMARY KEY,
  title NVARCHAR(255),
  original_title NVARCHAR(255),
  overview NVARCHAR(MAX),
  release_date DATE,
  popularity FLOAT,
  vote_average FLOAT,
  vote_count INT,
  runTime INT,
  adult BIT,
  certification NVARCHAR(255),
  backdrop_path NVARCHAR(255),
  poster_path NVARCHAR(255),
  video BIT,
  original_language NVARCHAR(2),
  director_id INT,
  CONSTRAINT CHK_Popularity CHECK (popularity >= 0),
  CONSTRAINT CHK_VoteAverage CHECK (vote_average >= 0 AND vote_average <= 10),
  CONSTRAINT CHK_VoteCount CHECK (vote_count >= 0),
  CONSTRAINT CHK_Adult CHECK (adult IN (0,1)),
  CONSTRAINT CHK_Video CHECK (video IN (0,1)),
  FOREIGN KEY (director_id) REFERENCES Person(ID),
);
CREATE TABLE movie_actor (
  movie_id INT,
  actor_id INT,
  FOREIGN KEY (movie_id) REFERENCES movies(movie_id),
  FOREIGN KEY (actor_id) REFERENCES Person(ID),
  PRIMARY KEY (movie_id, actor_id)
);

CREATE TABLE genres (
  genre_id INT PRIMARY KEY,
  genre_name NVARCHAR(50) UNIQUE
);

CREATE TABLE movie_genres (
  movie_id INT,
  genre_id INT,
  PRIMARY KEY (movie_id, genre_id),
  FOREIGN KEY (movie_id) REFERENCES movies(movie_id),
  FOREIGN KEY (genre_id) REFERENCES genres(genre_id)
);

-- Tạo bảng room
CREATE TABLE room (
  room_id INT PRIMARY KEY,
  cinema_id INT,
  FOREIGN KEY (cinema_id) REFERENCES cinemas(cinema_id)
); 


--50 cái 3 loại

-- Tạo bảng sch edule
CREATE TABLE schedule (
  schedule_id INT PRIMARY KEY IDENTITY,
  movie_id INT,
  room_id INT,
  schedule_date DATE,
  schedule_start TIME,
  FOREIGN KEY (movie_id) REFERENCES movies(movie_id),
  FOREIGN KEY (room_id) REFERENCES room(room_id)
);

-- Tạo bảng booking
CREATE TABLE booking (
  booking_id INT PRIMARY KEY IDENTITY,
  user_id INT,
  schedule_id INT,
  seat_id NVARCHAR(50),
  price FLOAT,
  FOREIGN KEY (user_id) REFERENCES users(user_id),
  FOREIGN KEY (schedule_id) REFERENCES schedule(schedule_id),
);

--Tạo bảng food
CREATE TABLE Food (
  FoodId INT PRIMARY KEY IDENTITY,
  FoodName NVARCHAR(255),
  FoodIdDescription NVARCHAR(MAX),
  Price FLOAT,
  ImagePath NVARCHAR(MAX)
);

--Tạo bảng booking food
CREATE TABLE bookingFood (
  booking_id INT PRIMARY KEY IDENTITY,
  user_id INT,
  schedule_id INT,
  FoodId INT,
  price FLOAT,
  FOREIGN KEY (user_id) REFERENCES users(user_id),
  FOREIGN KEY (schedule_id) REFERENCES schedule(schedule_id),
  FOREIGN KEY (FoodId) REFERENCES Food(FoodId),
);
