INSERT INTO Person (ID, Name, Avatar, Biography) VALUES (4441951, N'Josh Bonello', N'https://image.tmdb.org/t/p/originalNone', N'');
INSERT INTO Person (ID, Name, Avatar, Biography) VALUES (82511, N'Will Gluck', N'https://image.tmdb.org/t/p/original/facxxJZFpnoufJIY4jU9mC7RGKx.jpg', N'Will Gluck is an American film director, screenwriter and producer.');
INSERT INTO Person (ID, Name, Avatar, Biography) VALUES (466505, N'Kumail Nanjiani', N'https://image.tmdb.org/t/p/original/9EyrK1Cv7ey1h1GgmsVAOn45w6G.jpg', N'Kumail Ali Nanjiani (born May 2, 1978) is a Pakistani-American comedian, actor, and screenwriter. He is primarily known for his role as Dinesh in the HBO comedy series Silicon Valley (2014–2019) and for co-writing and starring in the romantic comedy film The Big Sick (2017). For co-writing the latter with his wife, Emily V. Gordon, he was nominated for the Academy Award for Best Original Screenplay. In 2018, Time magazine named him one of the 100 most influential people in the world.Nanjiani has also voiced Prismo on the animated series Adventure Time and starred in the TNT series Franklin & Bash and the Adult Swim series Newsreaders. He also co-hosted the Comedy Central show The Meltdown with Jonah and Kumail, as well as playing various roles on the comedy series Portlandia.');
INSERT INTO Person (ID, Name, Avatar, Biography) VALUES (9281, N'Elizabeth Banks', N'https://image.tmdb.org/t/p/original/zrkI1dYucpTM8Qydtrtro9MgQPb.jpg', N'Elizabeth Banks (born February 10, 1974) is an American actress, producer and director. She is known for playing Effie Trinket in The Hunger Games film series (2012–2015) and Gail Abernathy-McKadden in the Pitch Perfect film series (2012–2017). She made her directorial film debut with Pitch Perfect 2 (2015), whose $69 million opening-weekend gross set a record for a first-time director. She went on to direct, write, produce, and star in the action comedy film Charlies Angels (2019). She also directed and produced the horror comedy film Cocaine Bear (2023). Banks founded the film and television production company Brownstone Productions in October 2002, with her husband Max Handelman.Banks made her film debut in the low-budget independent film Surrender Dorothy (1998). She has appeared in the films Wet Hot American Summer (2001), Sam Raimis Spider-Man trilogy (2002–2007), The 40-Year-Old Virgin (2005), Invincible (2006), Zack and Miri Make a Porno (2008), Role Models (2008), The Next Three Days (2010), Man on a Ledge (2012), Movie 43 (2013), The Lego Movie (2014) and its 2019 sequel, Love & Mercy (2014), Walk of Shame (2014), Magic Mike XXL (2015), Power Rangers (2017), and The Beanie Bubble (2023).');
INSERT INTO Person (ID, Name, Avatar, Biography) VALUES (1736671, N'Caspar Jennings', N'https://image.tmdb.org/t/p/original/rf9tblH0sUOSWwWc8UDXCMmQWNV.jpg', N'');
