INSERT INTO Person (ID, Name, Avatar, Biography) VALUES (2069639, N'Chris Sandiford', N'https://image.tmdb.org/t/p/original/tqHXyyOqquUf4W7NHvcb72iU5Nz.jpg', N'Chris Sandiford is a Canadian comedian and actor who majored in Film Production at Concordia Universitys Mel Hoppenheim School of Cinema, Montreal, Quebec, Canada. Hes one half of the touring sketch comedy duo Ladies & Gentlemen.');
