INSERT INTO movies (movie_id, title, original_title, overview, release_date, popularity, vote_average, vote_count, adult, backdrop_path, poster_path, video, original_language, director_id, runTime) VALUES (1072790, N'Ghét Mà Vẫn Yêu', N'Anyone But You', N'Ghét Mà Vẫn Yêu xoay quanh Bea (Sydney Sweeney) và Ben (Glen Powell) - hai kẻ ghét nhau như “chó với mèo” từ thời Đại học. Sau nhiều năm tái ngộ, họ đều trở nên “ngon” hơn xưa gấp bội. Nhân chuyến du lịch Sydney (Úc), cả hai quyết định vờ làm người yêu và thực hiện luôn một đám cưới giả vì những lý do riêng. Thế nhưng, đây cũng là lúc vô số sự kiện dở khóc dở cười ập đến khiến hai kẻ thù dần nảy sinh tình cảm.', '2023-12-21', 1011.335, 6.967, 884, 0, 'https://image.tmdb.org/t/p/original/nTPFkLUARmo1bYHfkfdNpRKgEOs.jpg', 'https://image.tmdb.org/t/p/original/20gxhMH3wEsDliDuxAuCl7beviG.jpg', 0, 'en', 82511, 103);
