INSERT INTO movies (movie_id, title, original_title, overview, release_date, popularity, vote_average, vote_count, adult, backdrop_path, poster_path, video, original_language, director_id, runTime) VALUES (508883, N'Thiếu Niên Và Chim Diệc', N'君たちはどう生きるか', N'Đến từ Studio Ghibli và đạo diễn Miyazaki Hayao, bộ phim là câu chuyện về hành trình kỳ diệu của cậu thiếu niên Mahito trong một thế giới hoàn toàn mới lạ. Trải qua nỗi đau mất mẹ cùng mối quan hệ chẳng mấy vui vẻ với gia đình cũng như bạn học, Mahito dần cô lập bản thân... cho đến khi cậu gặp một chú chim diệc biết nói kỳ lạ. Mahito cùng chim diệc bước vào một tòa tháp bí ẩn, nơi một thế giới thần kỳ mở ra, đưa cậu gặp gỡ những người mình yêu thương... trong một nhân dạng hoàn toàn khác.', '2023-07-14', 294.556, 7.478, 1029, 0, 'https://image.tmdb.org/t/p/original/midCh3iVxaG3dUOrP9TnUpNcGm.jpg', 'https://image.tmdb.org/t/p/original/zC2AoYyY5JGHAT5Y5IidtqEkyVB.jpg', 0, 'ja', 608, 124);
